import { Card, Form, Button, Alert, Spinner, Table, Modal } from 'react-bootstrap';
import apiService from '../services/api.service';

const CollectionEditor = () => {
  const [collections, setCollections] = useState([]);
  const [selectedCollectionId, setSelectedCollectionId] = useState('');
  const [collectionItems, setCollectionItems] = useState([]);
  const [originalItems, setOriginalItems] = useState([]); // Store original structure
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [editingRequest, setEditingRequest] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editFormData, setEditFormData] = useState({
    headers: '',
    body: '',
    description: ''
  });

  useEffect(() => {
    loadCollections();
  }, []);

  useEffect(() => {
    if (selectedCollectionId) {
      loadCollectionRequests();
    } else {
      setCollectionItems([]);
    }
  }, [selectedCollectionId]);

  const loadCollections = async () => {
    try {
      const response = await apiService.get('/api/collections');
      setCollections(response.collections || []);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load collections' });
    }
  };

  const loadCollectionRequests = async () => {
    if (!selectedCollectionId) return;
    
    setLoading(true);
    setMessage({ type: '', text: '' });
    try {
      const response = await apiService.get(`/api/collections/${selectedCollectionId}/requests`);
      // Store original structure
      setOriginalItems(response.items || []);
      // Flatten items to get all requests (including nested in folders)
      const allRequests = flattenItems(response.items || []);
      setCollectionItems(allRequests);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load collection requests' });
    } finally {
      setLoading(false);
    }
  };

  const flattenItems = (items, parentPath = '') => {
    const requests = [];
    items.forEach((item, index) => {
      const currentPath = parentPath ? `${parentPath} > ${item.name}` : item.name;
      
      if (item.request) {
        // It's a request
        requests.push({
          ...item,
          id: `${parentPath}-${index}`,
          path: currentPath,
          originalIndex: index,
          parentPath: parentPath
        });
      } else if (item.item && Array.isArray(item.item)) {
        // It's a folder, recursively get requests
        const folderRequests = flattenItems(item.item, currentPath);
        requests.push(...folderRequests);
      }
    });
    return requests;
  };

  const handleEdit = (request) => {
    const headers = request.request?.header || [];
    const body = request.request?.body || {};
    const description = request.request?.description || '';
    
    setEditingRequest(request);
    setEditFormData({
      headers: JSON.stringify(headers, null, 2),
      body: body.raw || JSON.stringify(body, null, 2),
      description: description
    });
    setShowEditModal(true);
  };

  const handleSaveEdit = () => {
    if (!editingRequest) return;

    try {
      // Parse and update headers
      const headers = JSON.parse(editFormData.headers);
      
      // Update the request in collectionItems
      const updatedItems = collectionItems.map(item => {
        if (item.id === editingRequest.id) {
          return {
            ...item,
            request: {
              ...item.request,
              header: headers,
              body: item.request.body ? {
                ...item.request.body,
                raw: editFormData.body
              } : undefined,
              description: editFormData.description
            }
          };
        }
        return item;
      });
      
      setCollectionItems(updatedItems);
      setShowEditModal(false);
      setEditingRequest(null);
      setMessage({ type: 'success', text: 'Request updated (click Save Collection to persist)' });
    } catch (error) {
      setMessage({ type: 'danger', text: 'Invalid JSON format in headers or body' });
    }
  };

  const handleClone = (request) => {
    const clonedRequest = {
      ...request,
      name: `${request.name} (Copy)`,
      id: `${request.id}-${Date.now()}`
    };
    
    setCollectionItems([...collectionItems, clonedRequest]);
    setMessage({ type: 'success', text: 'Request cloned (click Save Collection to persist)' });
  };

  const handleDelete = (requestId) => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      const updatedItems = collectionItems.filter(item => item.id !== requestId);
      setCollectionItems(updatedItems);
      setMessage({ type: 'success', text: 'Request deleted (click Save Collection to persist)' });
    }
  };

  const rebuildCollectionStructure = (flatItems, originalItems) => {
    // Track which items from flatItems have been processed
    const processedIds = new Set();
    
    // Helper to find a request in flat items by matching name and method (excluding clones)
    const findUpdatedRequest = (originalRequest) => {
      return flatItems.find(flat => {
        // Skip if already processed or if it's a clone
        if (processedIds.has(flat.id) || flat.name.includes('(Copy)')) {
          return false;
        }
        return flat.name === originalRequest.name && 
               flat.request?.method === originalRequest.request?.method;
      });
    };

    // Recursively rebuild structure
    const rebuild = (items) => {
      const result = [];
      
      for (const originalItem of items) {
        // If it's a folder, recursively process its items
        if (originalItem.item && Array.isArray(originalItem.item)) {
          const rebuiltFolder = {
            ...originalItem,
            item: rebuild(originalItem.item)
          };
          result.push(rebuiltFolder);
        } 
        // If it's a request, find and use the updated version
        else if (originalItem.request) {
          const updatedItem = findUpdatedRequest(originalItem);
          if (updatedItem) {
            // Mark as processed
            processedIds.add(updatedItem.id);
            // Remove internal tracking fields
            const { id, path, originalIndex, parentPath, ...cleanItem } = updatedItem;
            result.push(cleanItem);
          } else {
            // Request was deleted, skip it
            continue;
          }
        }
        // Unknown type, keep as-is
        else {
          result.push(originalItem);
        }
      }
      
      return result;
    };

    const rebuiltItems = rebuild(originalItems);
    
    // Add any new cloned items that don't exist in original (only once per clone)
    const clonedItems = flatItems.filter(flatItem => {
      // Only process cloned items that haven't been processed
      if (!flatItem.name.includes('(Copy)') || processedIds.has(flatItem.id)) {
        return false;
      }
      
      // Check if this exact clone already exists in the result
      const alreadyAdded = rebuiltItems.some(item => {
        if (item.item && Array.isArray(item.item)) {
          // Check in folders recursively
          const checkInFolder = (items) => {
            return items.some(subItem => {
              if (subItem.item && Array.isArray(subItem.item)) {
                return checkInFolder(subItem.item);
              }
              return subItem.name === flatItem.name && 
                     subItem.request?.method === flatItem.request?.method;
            });
          };
          return checkInFolder(item.item);
        }
        return item.name === flatItem.name && 
               item.request?.method === flatItem.request?.method;
      });
      
      return !alreadyAdded;
    });
    
    // Add cloned items at root level (in the same folder as original if we can determine it)
    clonedItems.forEach(clonedItem => {
      // Remove internal tracking fields
      const { id, path, originalIndex, parentPath, ...cleanItem } = clonedItem;
      // Add to root level for now (can be enhanced to add to same folder as original)
      rebuiltItems.push(cleanItem);
    });
    
    return rebuiltItems;
  };

  const handleSaveCollection = async () => {
    if (!selectedCollectionId) {
      setMessage({ type: 'warning', text: 'Please select a collection first' });
      return;
    }

    setSaving(true);
    setMessage({ type: '', text: '' });

    try {
      // Rebuild collection structure from flat items, preserving original folder structure
      const items = rebuildCollectionStructure(collectionItems, originalItems);
      
      await apiService.put(`/api/collections/${selectedCollectionId}`, { items });
      setMessage({ type: 'success', text: 'Collection saved successfully!' });
      
      // Reload to get updated structure
      await loadCollectionRequests();
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to save collection' });
    } finally {
      setSaving(false);
    }
  };

  return (
    <div>
      <h2 className="mb-4">Collection Editor</h2>
      
      {message.text && (
        <Alert variant={message.type} dismissible onClose={() => setMessage({ type: '', text: '' })}>
          {message.text}
        </Alert>
      )}

      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Select Collection</h5>
        </Card.Header>
        <Card.Body>
          <Form.Group>
            <Form.Label>Postman Collection</Form.Label>
            <Form.Select
              value={selectedCollectionId}
              onChange={(e) => setSelectedCollectionId(e.target.value)}
            >
              <option value="">-- Select a collection --</option>
              {collections.map((collection) => (
                <option key={collection.id} value={collection.id}>
                  {collection.name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Card.Body>
      </Card>

      {selectedCollectionId && (
        <Card>
          <Card.Header className="d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Requests</h5>
            <Button
              variant="primary"
              onClick={handleSaveCollection}
              disabled={saving || collectionItems.length === 0}
            >
              {saving ? (
                <>
                  <Spinner animation="border" size="sm" className="me-2" />
                  Saving...
                </>
              ) : (
                'Save Collection'
              )}
            </Button>
          </Card.Header>
          <Card.Body>
            {loading ? (
              <div className="text-center py-4">
                <Spinner animation="border" />
                <p className="mt-2">Loading requests...</p>
              </div>
            ) : collectionItems.length === 0 ? (
              <Alert variant="info">No requests found in this collection.</Alert>
            ) : (
              <div className="table-responsive">
                <Table striped bordered hover>
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Method</th>
                      <th>URL</th>
                      <th>Path</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {collectionItems.map((request) => (
                      <tr key={request.id}>
                        <td>{request.name}</td>
                        <td>
                          <span className="badge bg-primary">
                            {request.request?.method || 'N/A'}
                          </span>
                        </td>
                        <td className="text-truncate" style={{ maxWidth: '300px' }}>
                          {request.request?.url?.raw || 'N/A'}
                        </td>
                        <td className="text-muted small">{request.path}</td>
                        <td>
                          <Button
                            variant="outline-primary"
                            size="sm"
                            className="me-2"
                            onClick={() => handleEdit(request)}
                          >
                            Edit
                          </Button>
                          <Button
                            variant="outline-success"
                            size="sm"
                            className="me-2"
                            onClick={() => handleClone(request)}
                          >
                            Clone
                          </Button>
                          <Button
                            variant="outline-danger"
                            size="sm"
                            onClick={() => handleDelete(request.id)}
                          >
                            Delete
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </div>
            )}
          </Card.Body>
        </Card>
      )}

      {/* Edit Modal */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Edit Request: {editingRequest?.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={editFormData.description}
                onChange={(e) => setEditFormData({ ...editFormData, description: e.target.value })}
                placeholder="Request description"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Headers (JSON)</Form.Label>
              <Form.Control
                as="textarea"
                rows={6}
                value={editFormData.headers}
                onChange={(e) => setEditFormData({ ...editFormData, headers: e.target.value })}
                placeholder='[{"key": "Content-Type", "value": "application/json", "type": "text"}]'
              />
              <Form.Text className="text-muted">
                Enter headers as JSON array
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Body</Form.Label>
              <Form.Control
                as="textarea"
                rows={8}
                value={editFormData.body}
                onChange={(e) => setEditFormData({ ...editFormData, body: e.target.value })}
                placeholder="Request body content"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSaveEdit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default CollectionEditor;
