import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert, Spinner, Table, Badge, Accordion, Modal, Row, Col } from 'react-bootstrap';
import apiService from '../services/api.service';

const GridFiltering = () => {
  // Section 1: Collection and Endpoint Selection
  const [collections, setCollections] = useState([]);
  const [selectedCollectionId, setSelectedCollectionId] = useState('');
  const [requests, setRequests] = useState([]);
  const [selectedRequestId, setSelectedRequestId] = useState('');
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingRequests, setLoadingRequests] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  // Section 2: Request and Response Display
  const [requestBody, setRequestBody] = useState(null);
  const [responseBody, setResponseBody] = useState(null);
  const [selectedResponse, setSelectedResponse] = useState(null);
  const [responses, setResponses] = useState([]);

  // Section 3: Mapping Area
  const [requestAttributes, setRequestAttributes] = useState({}); // {attribute: {value, type, path}}
  const [responseAttributes, setResponseAttributes] = useState({}); // {attribute: {value, type, path}}
  const [mappings, setMappings] = useState({}); // {requestAttribute: responseAttribute}

  // Section 5: Collection Generation
  const [generating, setGenerating] = useState(false);
  const [newCollectionName, setNewCollectionName] = useState('');

  useEffect(() => {
    loadCollections();
  }, []);

  useEffect(() => {
    if (selectedCollectionId) {
      loadRequests();
    } else {
      setRequests([]);
      setSelectedRequestId('');
      setSelectedRequest(null);
    }
  }, [selectedCollectionId]);

  useEffect(() => {
    if (selectedRequestId && requests.length > 0) {
      const request = requests.find(r => r.id === selectedRequestId);
      if (request) {
        setSelectedRequest(request);
        loadRequestDetails(request);
      }
    } else {
      setSelectedRequest(null);
      setRequestBody(null);
      setResponseBody(null);
      setResponses([]);
      setMappings({});
      setRequestAttributes({});
      setResponseAttributes({});
    }
  }, [selectedRequestId, requests]);

  useEffect(() => {
    if (selectedRequest) {
      loadRequestWithResponses();
    } else {
      setResponses([]);
      setResponseBody(null);
    }
  }, [selectedRequest, selectedCollectionId]);

  const loadCollections = async () => {
    try {
      const response = await apiService.get('/api/collections');
      setCollections(response.collections || []);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load collections' });
    }
  };

  const loadRequests = async () => {
    if (!selectedCollectionId) return;
    
    setLoadingRequests(true);
    setMessage({ type: '', text: '' });
    try {
      const response = await apiService.get(`/api/collections/${selectedCollectionId}/requests`);
      const allRequests = flattenItems(response.items || []);
      setRequests(allRequests);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load requests' });
    } finally {
      setLoadingRequests(false);
    }
  };

  const flattenItems = (items, parentPath = '') => {
    const requests = [];
    items.forEach((item, index) => {
      const currentPath = parentPath ? `${parentPath} > ${item.name}` : item.name;
      
      if (item.request) {
        requests.push({
          ...item,
          id: `${parentPath}-${index}-${Date.now()}`,
          path: currentPath
        });
      } else if (item.item && Array.isArray(item.item)) {
        const folderRequests = flattenItems(item.item, currentPath);
        requests.push(...folderRequests);
      }
    });
    return requests;
  };

  const loadRequestDetails = async (request) => {
    if (!request || !request.request) return;

    try {
      const req = request.request;
      const url = req.url || {};
      const body = req.body || {};

      // Parse request body
      let parsedBody = null;
      if (body.raw) {
        try {
          parsedBody = JSON.parse(body.raw);
        } catch (e) {
          parsedBody = body.raw;
        }
      } else if (body.formdata) {
        parsedBody = body.formdata;
      } else if (body.urlencoded) {
        parsedBody = body.urlencoded;
      }

      setRequestBody({
        raw: body.raw || '',
        parsed: parsedBody,
        mode: body.mode || 'raw'
      });

      // Extract attributes from request body
      if (parsedBody && typeof parsedBody === 'object') {
        try {
          const response = await apiService.post('/api/collections/extract-attributes', parsedBody);
          setRequestAttributes(response.attributes || {});
          // Initialize mappings object with request attributes
          const initialMappings = {};
          Object.keys(response.attributes || {}).forEach(attr => {
            initialMappings[attr] = '';
          });
          setMappings(initialMappings);
        } catch (error) {
          console.error('Failed to extract request attributes:', error);
          // Fallback: extract manually
          const attributes = extractAttributesManually(parsedBody);
          setRequestAttributes(attributes);
          const initialMappings = {};
          Object.keys(attributes).forEach(attr => {
            initialMappings[attr] = '';
          });
          setMappings(initialMappings);
        }
      } else {
        setRequestAttributes({});
        setMappings({});
      }
    } catch (error) {
      console.error('Failed to load request details:', error);
    }
  };

  const loadRequestWithResponses = async () => {
    if (!selectedCollectionId || !selectedRequest) return;

    try {
      const fullResponse = await apiService.get(`/api/collections/${selectedCollectionId}/full`);
      const fullCollection = fullResponse.items || [];
      
      const foundRequest = findRequestInFullCollection(fullCollection, selectedRequest);
      
      if (foundRequest && foundRequest.response && foundRequest.response.length > 0) {
        setResponses(foundRequest.response);
        // Default to 200 response if available
        const successResponse = foundRequest.response.find(r => (r.code || r.status) === 200) || foundRequest.response[0];
        if (successResponse) {
          handleResponseSelect(successResponse);
        }
      } else {
        setResponses([]);
        setResponseBody(null);
      }
    } catch (error) {
      console.error('Failed to load responses:', error);
      setResponses([]);
    }
  };

  const findRequestInFullCollection = (items, targetRequest) => {
    if (!targetRequest || !targetRequest.request) return null;

    const targetName = targetRequest.name || '';
    const targetMethod = (targetRequest.request?.method || '').toUpperCase();

    const findRecursive = (items) => {
      for (const item of items) {
        if (item.item && Array.isArray(item.item)) {
          const found = findRecursive(item.item);
          if (found) return found;
        } else if (item.request) {
          const itemName = item.name || '';
          const itemMethod = (item.request.method || '').toUpperCase();
          
          if (itemName === targetName && itemMethod === targetMethod) {
            return item;
          }
        }
      }
      return null;
    };

    return findRecursive(items);
  };

  const handleResponseSelect = async (response) => {
    setSelectedResponse(response);
    
    // Parse response body
    let parsedBody = null;
    const body = response.body || '';
    
    try {
      if (typeof body === 'string') {
        parsedBody = JSON.parse(body);
      } else {
        parsedBody = body;
      }
    } catch (e) {
      // Not JSON, use as string
      parsedBody = body;
    }

    setResponseBody({
      raw: typeof body === 'string' ? body : JSON.stringify(body, null, 2),
      parsed: parsedBody
    });

    // Extract attributes from response body
    if (parsedBody && typeof parsedBody === 'object') {
      try {
        const response = await apiService.post('/api/collections/extract-attributes', parsedBody);
        setResponseAttributes(response.attributes || {});
      } catch (error) {
        console.error('Failed to extract attributes:', error);
        // Fallback: extract manually
        const attributes = extractAttributesManually(parsedBody);
        setResponseAttributes(attributes);
      }
    } else {
      setResponseAttributes({});
    }

    // Reset mappings when response changes (keep request attributes structure)
    const updatedMappings = {};
    Object.keys(mappings).forEach(requestAttr => {
      updatedMappings[requestAttr] = '';
    });
    setMappings(updatedMappings);
  };

  const extractAttributesManually = (data, prefix = '') => {
    const attributes = {};
    
    if (Array.isArray(data) && data.length > 0 && typeof data[0] === 'object') {
      // Extract from first object in array
      return extractAttributesManually(data[0], prefix);
    }
    
    if (typeof data === 'object' && data !== null) {
      for (const [key, value] of Object.entries(data)) {
        const fullKey = prefix ? `${prefix}.${key}` : key;
        
        if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
          // Nested object - recurse
          const nested = extractAttributesManually(value, fullKey);
          Object.assign(attributes, nested);
        } else if (Array.isArray(value) && value.length > 0 && typeof value[0] === 'object') {
          // Array of objects - extract from first
          const nested = extractAttributesManually(value[0], fullKey);
          Object.assign(attributes, nested);
        } else {
          // Leaf value
          let type = 'string';
          if (typeof value === 'boolean') type = 'boolean';
          else if (typeof value === 'number') type = 'number';
          
          attributes[fullKey] = {
            value: value,
            type: type,
            path: fullKey
          };
        }
      }
    }
    
    return attributes;
  };

  const updateMapping = (requestAttribute, responseAttribute) => {
    setMappings({
      ...mappings,
      [requestAttribute]: responseAttribute
    });
  };

  const handleGenerateCollection = async () => {
    if (!selectedRequest || !responseBody || !responseBody.parsed) {
      setMessage({ type: 'warning', text: 'Please select a request and response first' });
      return;
    }

    // Convert mappings object to array format for API
    const mappingsArray = Object.entries(mappings)
      .filter(([requestAttr, responseAttr]) => requestAttr && responseAttr)
      .map(([requestAttr, responseAttr]) => ({
        responseAttribute: responseAttr,
        requestField: requestAttr
      }));

    if (mappingsArray.length === 0) {
      setMessage({ type: 'warning', text: 'Please add at least one mapping' });
      return;
    }

    setGenerating(true);
    setMessage({ type: '', text: '' });

    try {
      const collectionName = newCollectionName || `Mapped_${selectedRequest.name}_${Date.now()}`;
      
      const response = await apiService.post('/api/collections/generate-filtered', {
        collection_id: selectedCollectionId,
        request_name: selectedRequest.name,
        request_method: selectedRequest.request?.method || 'GET',
        response_body: responseBody.parsed,
        mappings: mappingsArray,
        filters: [], // No filters needed
        collection_name: collectionName
      });

      setMessage({ 
        type: 'success', 
        text: `Collection "${response.name}" generated successfully!` 
      });
      
      // Reset form
      setNewCollectionName('');
      
      // Reload collections to show new one
      await loadCollections();
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to generate collection' });
    } finally {
      setGenerating(false);
    }
  };

  return (
    <div>
      <h2 className="mb-4">Postman Grid Filtering</h2>
      
      {message.text && (
        <Alert variant={message.type} dismissible onClose={() => setMessage({ type: '', text: '' })}>
          {message.text}
        </Alert>
      )}

      {/* Section 1: Collection and Endpoint Selection */}
      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Section 1: Select Collection and Endpoint</h5>
        </Card.Header>
        <Card.Body>
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Postman Collection</Form.Label>
                <Form.Select
                  value={selectedCollectionId}
                  onChange={(e) => setSelectedCollectionId(e.target.value)}
                  disabled={loading}
                >
                  <option value="">-- Select a collection --</option>
                  {collections.map((collection) => (
                    <option key={collection.id} value={collection.id}>
                      {collection.name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Endpoint</Form.Label>
                {loadingRequests ? (
                  <div className="text-center py-2">
                    <Spinner animation="border" size="sm" className="me-2" />
                    Loading endpoints...
                  </div>
                ) : (
                  <Form.Select
                    value={selectedRequestId}
                    onChange={(e) => setSelectedRequestId(e.target.value)}
                    disabled={!selectedCollectionId}
                  >
                    <option value="">-- Select an endpoint --</option>
                    {requests.map((request) => (
                      <option key={request.id} value={request.id}>
                        {request.name} ({request.request?.method || 'N/A'})
                      </option>
                    ))}
                  </Form.Select>
                )}
              </Form.Group>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {selectedRequest && (
        <>
          {/* Section 2: Request and Response Display */}
          <Row className="mb-4">
            <Col md={6}>
              <Card>
                <Card.Header>
                  <h5 className="mb-0">Section 2: Request Body</h5>
                </Card.Header>
                <Card.Body>
                  {requestBody ? (
                    <div>
                      <pre style={{ 
                        maxHeight: '400px', 
                        overflow: 'auto', 
                        backgroundColor: '#f8f9fa', 
                        padding: '15px', 
                        borderRadius: '5px',
                        fontSize: '12px'
                      }}>
                        {typeof requestBody.parsed === 'object' 
                          ? JSON.stringify(requestBody.parsed, null, 2)
                          : requestBody.raw}
                      </pre>
                    </div>
                  ) : (
                    <Alert variant="info">No request body available</Alert>
                  )}
                </Card.Body>
              </Card>
            </Col>
            <Col md={6}>
              <Card>
                <Card.Header className="d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">Section 2: Response Body</h5>
                  {responses.length > 0 && (
                    <Form.Select
                      size="sm"
                      style={{ width: 'auto' }}
                      value={selectedResponse ? `${selectedResponse.code || selectedResponse.status}` : ''}
                      onChange={(e) => {
                        const status = e.target.value;
                        const found = responses.find(r => String(r.code || r.status) === status);
                        if (found) handleResponseSelect(found);
                      }}
                    >
                      <option value="">Select response...</option>
                      {responses.map((r, idx) => (
                        <option key={idx} value={r.code || r.status}>
                          {r.code || r.status} - {r.name || `Response ${idx + 1}`}
                        </option>
                      ))}
                    </Form.Select>
                  )}
                </Card.Header>
                <Card.Body>
                  {responseBody ? (
                    <div>
                      <pre style={{ 
                        maxHeight: '400px', 
                        overflow: 'auto', 
                        backgroundColor: '#f8f9fa', 
                        padding: '15px', 
                        borderRadius: '5px',
                        fontSize: '12px'
                      }}>
                        {typeof responseBody.parsed === 'object' 
                          ? JSON.stringify(responseBody.parsed, null, 2)
                          : responseBody.raw}
                      </pre>
                    </div>
                  ) : (
                    <Alert variant="info">
                      {responses.length > 0 
                        ? 'Please select a response from the dropdown above'
                        : 'No response examples available for this request'}
                    </Alert>
                  )}
                </Card.Body>
              </Card>
            </Col>
          </Row>

          {/* Section 3: Mapping Area */}
          {requestBody && Object.keys(requestAttributes).length > 0 && responseBody && Object.keys(responseAttributes).length > 0 && (
            <Card className="mb-4">
              <Card.Header>
                <h5 className="mb-0">Section 3: Mapping Area</h5>
              </Card.Header>
              <Card.Body>
                <Card className="mb-3" style={{ backgroundColor: '#f8f9fa' }}>
                  <Card.Body>
                    <Card.Title className="h6 mb-3">Sub-Section 1: Assign Dynamic Values for Request Body from Response Body</Card.Title>
                    <Table striped bordered hover size="sm">
                      <thead>
                        <tr>
                          <th style={{ width: '40%' }}>Request Body Attribute</th>
                          <th style={{ width: '60%' }}>Map from Response Body Attribute</th>
                        </tr>
                      </thead>
                      <tbody>
                        {Object.entries(requestAttributes).map(([requestAttr, requestData]) => (
                          <tr key={requestAttr}>
                            <td>
                              <div>
                                <strong>{requestAttr}</strong>
                                <Badge bg={requestData.type === 'string' ? 'primary' : requestData.type === 'number' ? 'success' : 'warning'} className="ms-2">
                                  {requestData.type}
                                </Badge>
                              </div>
                            </td>
                            <td>
                              <Form.Select
                                size="sm"
                                value={mappings[requestAttr] || ''}
                                onChange={(e) => updateMapping(requestAttr, e.target.value)}
                              >
                                <option value="">-- Select response attribute --</option>
                                {Object.keys(responseAttributes).map(responseAttr => (
                                  <option key={responseAttr} value={responseAttr}>
                                    {responseAttr} ({responseAttributes[responseAttr].type})
                                  </option>
                                ))}
                              </Form.Select>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </Card.Body>
                </Card>
              </Card.Body>
            </Card>
          )}

          {/* Section 4: Generate Collection */}
          {requestBody && responseBody && Object.keys(mappings).some(key => mappings[key]) && (
            <Card className="mb-4">
              <Card.Header>
                <h5 className="mb-0">Section 4: Generate Collection</h5>
              </Card.Header>
              <Card.Body>
                <Form.Group className="mb-3">
                  <Form.Label>New Collection Name (Optional)</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Leave empty for auto-generated name"
                    value={newCollectionName}
                    onChange={(e) => setNewCollectionName(e.target.value)}
                  />
                  <Form.Text className="text-muted">
                    If not specified, a name will be auto-generated with timestamp
                  </Form.Text>
                </Form.Group>
                
                <div className="mb-3">
                  <Alert variant="info">
                    <strong>Summary:</strong>
                    <ul className="mb-0 mt-2">
                      <li>Request Attributes: {Object.keys(requestAttributes).length}</li>
                      <li>Response Attributes: {Object.keys(responseAttributes).length}</li>
                      <li>Mappings: {Object.values(mappings).filter(m => m).length}</li>
                    </ul>
                  </Alert>
                </div>

                <Button
                  variant="success"
                  size="lg"
                  onClick={handleGenerateCollection}
                  disabled={generating || Object.values(mappings).filter(m => m).length === 0}
                >
                  {generating ? (
                    <>
                      <Spinner animation="border" size="sm" className="me-2" />
                      Generating Collection...
                    </>
                  ) : (
                    'Generate and Save Collection'
                  )}
                </Button>
              </Card.Body>
            </Card>
          )}
        </>
      )}
    </div>
  );
};

export default GridFiltering;
