import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert, Spinner, Table, Badge, Accordion, Modal } from 'react-bootstrap';
import apiService from '../services/api.service';

const GridFiltering = () => {
  const [collections, setCollections] = useState([]);
  const [selectedCollectionId, setSelectedCollectionId] = useState('');
  const [requests, setRequests] = useState([]);
  const [selectedRequestId, setSelectedRequestId] = useState('');
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [fullRequest, setFullRequest] = useState(null); // Full request with responses
  const [loading, setLoading] = useState(false);
  const [loadingRequests, setLoadingRequests] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  
  // Editable request details
  const [requestDetails, setRequestDetails] = useState({
    name: '',
    method: '',
    url: '',
    headers: '',
    body: '',
    description: '',
    queryParams: ''
  });
  
  // Responses
  const [responses, setResponses] = useState([]);
  const [selectedResponse, setSelectedResponse] = useState(null);
  const [selectedResponseStatus, setSelectedResponseStatus] = useState('');
  const [filteredResponse, setFilteredResponse] = useState(null);
  const [showResponseModal, setShowResponseModal] = useState(false);

  useEffect(() => {
    loadCollections();
  }, []);

  useEffect(() => {
    if (selectedCollectionId) {
      loadRequests();
    } else {
      setRequests([]);
      setSelectedRequestId('');
      setSelectedRequest(null);
      setRequestDetails({
        name: '',
        method: '',
        url: '',
        headers: '',
        body: '',
        description: '',
        queryParams: ''
      });
    }
  }, [selectedCollectionId]);

  useEffect(() => {
    if (selectedRequestId && requests.length > 0) {
      const request = requests.find(r => r.id === selectedRequestId);
      if (request) {
        setSelectedRequest(request);
        loadRequestDetails(request);
      }
    } else {
      setSelectedRequest(null);
      setFullRequest(null);
      setResponses([]);
      setRequestDetails({
        name: '',
        method: '',
        url: '',
        headers: '',
        body: '',
        description: '',
        queryParams: ''
      });
    }
  }, [selectedRequestId, requests]);

  useEffect(() => {
    if (selectedRequest) {
      loadRequestWithResponses();
    } else {
      setResponses([]);
      setFullRequest(null);
    }
  }, [selectedRequest, selectedCollectionId]);

  const loadCollections = async () => {
    try {
      const response = await apiService.get('/api/collections');
      setCollections(response.collections || []);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load collections' });
    }
  };

  const loadRequests = async () => {
    if (!selectedCollectionId) return;
    
    setLoadingRequests(true);
    setMessage({ type: '', text: '' });
    try {
      const response = await apiService.get(`/api/collections/${selectedCollectionId}/requests`);
      const allRequests = flattenItems(response.items || []);
      setRequests(allRequests);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load requests' });
    } finally {
      setLoadingRequests(false);
    }
  };

  const flattenItems = (items, parentPath = '') => {
    const requests = [];
    items.forEach((item, index) => {
      const currentPath = parentPath ? `${parentPath} > ${item.name}` : item.name;
      
      if (item.request) {
        // It's a request
        requests.push({
          ...item,
          id: `${parentPath}-${index}-${Date.now()}`,
          path: currentPath
        });
      } else if (item.item && Array.isArray(item.item)) {
        // It's a folder, recursively get requests
        const folderRequests = flattenItems(item.item, currentPath);
        requests.push(...folderRequests);
      }
    });
    return requests;
  };

  const loadRequestDetails = (request) => {
    if (!request || !request.request) return;

    try {
      const req = request.request;
      const url = req.url || {};
      const headers = req.header || [];
      const body = req.body || {};
      const queryParams = url.query || [];

      setRequestDetails({
        name: request.name || '',
        method: req.method || '',
        url: url.raw || '',
        headers: JSON.stringify(headers, null, 2),
        body: body.raw || (body ? JSON.stringify(body, null, 2) : ''),
        description: req.description || '',
        queryParams: JSON.stringify(queryParams, null, 2)
      });
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load request details' });
    }
  };

  const loadRequestWithResponses = async () => {
    if (!selectedCollectionId || !selectedRequest) {
      console.log('Missing requirements:', { selectedCollectionId, selectedRequest });
      return;
    }

    try {
      console.log('Loading responses for request:', selectedRequest.name, selectedRequest);
      
      // First, check if the selected request already has responses
      if (selectedRequest.response && Array.isArray(selectedRequest.response) && selectedRequest.response.length > 0) {
        console.log('Request already has responses:', selectedRequest.response.length);
        setFullRequest(selectedRequest);
        setResponses(selectedRequest.response);
        setSelectedResponseStatus('');
        setFilteredResponse(null);
        return;
      }
      
      // Load full collection to get responses
      const fullResponse = await apiService.get(`/api/collections/${selectedCollectionId}/full`);
      const fullCollection = fullResponse.items || [];
      
      console.log('Full collection loaded, items count:', fullCollection.length);
      
      // Find the request in the full collection to get responses
      const foundRequest = findRequestInFullCollection(fullCollection, selectedRequest);
      
      if (foundRequest) {
        console.log('Request found! Response count:', foundRequest.response?.length || 0);
        setFullRequest(foundRequest);
        const requestResponses = foundRequest.response || [];
        console.log('Setting responses:', requestResponses.length);
        
        if (requestResponses.length > 0) {
          console.log('Response details:', requestResponses.map(r => ({
            name: r.name,
            code: r.code,
            status: r.status,
            hasBody: !!r.body
          })));
        }
        
        setResponses(requestResponses);
        // Reset response status filter when loading new request
        setSelectedResponseStatus('');
        setFilteredResponse(null);
      } else {
        console.log('Request not found in full collection');
        // Try to find by just name (more lenient matching)
        const foundByName = findRequestByNameOnly(fullCollection, selectedRequest.name);
        if (foundByName && foundByName.response && foundByName.response.length > 0) {
          console.log('Found request by name only, using its responses');
          setFullRequest(foundByName);
          setResponses(foundByName.response);
          setSelectedResponseStatus('');
          setFilteredResponse(null);
        } else {
          setFullRequest(null);
          setResponses([]);
          setSelectedResponseStatus('');
          setFilteredResponse(null);
        }
      }
    } catch (error) {
      console.error('Failed to load request responses:', error);
      setMessage({ type: 'danger', text: `Failed to load responses: ${error.message || 'Unknown error'}` });
      setResponses([]);
    }
  };

  const findRequestByNameOnly = (items, targetName) => {
    const findRecursive = (items) => {
      for (const item of items) {
        if (item.item && Array.isArray(item.item)) {
          const found = findRecursive(item.item);
          if (found) return found;
        } else if (item.request && item.name === targetName) {
          return item;
        }
      }
      return null;
    };
    return findRecursive(items);
  };

  const findRequestInFullCollection = (items, targetRequest) => {
    if (!targetRequest || !targetRequest.request) {
      console.log('Invalid target request:', targetRequest);
      return null;
    }

    const targetName = targetRequest.name || '';
    const targetMethod = (targetRequest.request?.method || '').toUpperCase();
    
    console.log('Searching for request:', { 
      targetName, 
      targetMethod,
      targetRequestPath: targetRequest.path 
    });

    const findRecursive = (items, depth = 0, parentPath = '') => {
      for (const item of items) {
        const currentPath = parentPath ? `${parentPath} > ${item.name}` : item.name;
        
        if (item.item && Array.isArray(item.item)) {
          // It's a folder - recurse
          const found = findRecursive(item.item, depth + 1, currentPath);
          if (found) return found;
        } else if (item.request) {
          // It's a request - check if it matches
          const itemName = item.name || '';
          const itemMethod = (item.request.method || '').toUpperCase();
          
          // Try multiple matching strategies
          const exactNameMatch = itemName === targetName;
          const trimmedNameMatch = itemName.trim() === targetName.trim();
          const methodMatch = itemMethod === targetMethod;
          
          // Also check if path matches (if available)
          const pathMatch = !targetRequest.path || currentPath.includes(targetRequest.path) || targetRequest.path.includes(currentPath);
          
          if ((exactNameMatch || trimmedNameMatch) && methodMatch) {
            console.log('Found matching request:', { 
              itemName, 
              itemMethod, 
              currentPath,
              hasResponse: !!item.response, 
              responseCount: item.response?.length || 0,
              responseKeys: item.response ? Object.keys(item.response[0] || {}) : []
            });
            
            // Log response structure for debugging
            if (item.response && item.response.length > 0) {
              console.log('First response sample:', {
                name: item.response[0].name,
                code: item.response[0].code,
                status: item.response[0].status,
                hasBody: !!item.response[0].body,
                bodyType: typeof item.response[0].body,
                bodyLength: typeof item.response[0].body === 'string' ? item.response[0].body.length : 'N/A'
              });
            }
            
            return item;
          }
        }
      }
      return null;
    };

    const found = findRecursive(items);
    if (!found) {
      // Log available requests for debugging
      const availableRequests = [];
      const logRequests = (items, path = '') => {
        items.forEach(item => {
          if (item.request) {
            availableRequests.push({
              name: item.name,
              method: item.request.method,
              path: path,
              hasResponse: !!item.response,
              responseCount: item.response?.length || 0
            });
          } else if (item.item) {
            logRequests(item.item, path ? `${path} > ${item.name}` : item.name);
          }
        });
      };
      logRequests(items);
      console.log('Request not found. Available requests:', availableRequests);
    }
    return found;
  };

  const handleSaveRequest = async () => {
    if (!selectedCollectionId || !selectedRequestId || !selectedRequest) {
      setMessage({ type: 'warning', text: 'Please select a collection and request first' });
      return;
    }

    setSaving(true);
    setMessage({ type: '', text: '' });

    try {
      // Parse headers and query params
      let headers = [];
      let queryParams = [];
      
      try {
        if (requestDetails.headers.trim()) {
          headers = JSON.parse(requestDetails.headers);
          if (!Array.isArray(headers)) {
            throw new Error('Headers must be a JSON array');
          }
        }
      } catch (e) {
        setMessage({ type: 'danger', text: 'Invalid JSON format in headers. Must be a valid JSON array.' });
        setSaving(false);
        return;
      }

      try {
        if (requestDetails.queryParams.trim()) {
          queryParams = JSON.parse(requestDetails.queryParams);
          if (!Array.isArray(queryParams)) {
            throw new Error('Query params must be a JSON array');
          }
        }
      } catch (e) {
        setMessage({ type: 'danger', text: 'Invalid JSON format in query params. Must be a valid JSON array.' });
        setSaving(false);
        return;
      }

      // Load full collection to update
      const fullResponse = await apiService.get(`/api/collections/${selectedCollectionId}/full`);
      const fullCollection = {
        info: fullResponse.collection_info || {},
        item: fullResponse.items || []
      };

      // Find and update the request in the collection
      const updated = updateRequestInCollection(
        fullCollection.item,
        selectedRequest,
        requestDetails,
        headers,
        queryParams
      );

      if (!updated) {
        setMessage({ type: 'danger', text: 'Request not found in collection' });
        setSaving(false);
        return;
      }

      // Save the updated collection
      await apiService.put(`/api/collections/${selectedCollectionId}`, {
        collection: fullCollection
      });

      setMessage({ type: 'success', text: 'Request updated successfully!' });
      
      // Reload requests to reflect changes
      await loadRequests();
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to save request' });
    } finally {
      setSaving(false);
    }
  };

  const updateRequestInCollection = (items, targetRequest, details, headers, queryParams) => {
    const findAndUpdate = (items) => {
      for (const item of items) {
        if (item.item && Array.isArray(item.item)) {
          // It's a folder - recurse
          if (findAndUpdate(item.item)) {
            return true;
          }
        } else if (item.request) {
          // It's a request - check if it matches
          if (item.name === targetRequest.name && 
              item.request.method === targetRequest.request?.method) {
            // Update the request
            item.name = details.name;
            item.request.method = details.method;
            item.request.description = details.description;
            item.request.header = headers;
            
            // Update URL
            if (item.request.url) {
              item.request.url.raw = details.url;
              item.request.url.query = queryParams;
              
              // Parse URL - handle Postman variables like {{baseUrl}}
              try {
                // Check if URL contains variables (e.g., {{baseUrl}})
                if (details.url.includes('{{')) {
                  // For URLs with variables, try to parse manually
                  const urlMatch = details.url.match(/^(https?:\/\/[^\/]+|{{[^}]+}})(\/.*)?$/);
                  if (urlMatch) {
                    const hostPart = urlMatch[1];
                    const pathPart = urlMatch[2] || '';
                    
                    if (hostPart.startsWith('{{')) {
                      // Variable-based URL
                      item.request.url.host = [hostPart];
                    } else {
                      // Regular URL
                      item.request.url.host = [hostPart.replace(/^https?:\/\//, '')];
                    }
                    
                    // Parse path
                    if (pathPart) {
                      item.request.url.path = pathPart.split('/').filter(p => p && !p.startsWith('{{'));
                    } else {
                      item.request.url.path = [];
                    }
                  }
                } else {
                  // Regular URL without variables
                  const urlObj = new URL(details.url);
                  item.request.url.host = [urlObj.hostname];
                  item.request.url.path = urlObj.pathname.split('/').filter(p => p);
                }
              } catch (e) {
                // If URL parsing fails, try to parse manually
                const urlMatch = details.url.match(/^(https?:\/\/[^\/]+|{{[^}]+}})(\/.*)?$/);
                if (urlMatch) {
                  const hostPart = urlMatch[1];
                  if (hostPart.startsWith('{{')) {
                    item.request.url.host = [hostPart];
                  } else {
                    item.request.url.host = [hostPart.replace(/^https?:\/\//, '')];
                  }
                  const pathPart = urlMatch[2] || '';
                  item.request.url.path = pathPart ? pathPart.split('/').filter(p => p && !p.startsWith('{{')) : [];
                }
              }
            }
            
            // Update body
            if (details.body && details.body.trim()) {
              if (item.request.body) {
                item.request.body.raw = details.body;
              } else {
                item.request.body = {
                  mode: 'raw',
                  raw: details.body,
                  options: {
                    raw: {
                      language: 'json'
                    }
                  }
                };
              }
            }
            
            return true;
          }
        }
      }
      return false;
    };

    return findAndUpdate(items);
  };

  return (
    <div>
      <h2 className="mb-4">Grid Filtering</h2>
      
      {message.text && (
        <Alert variant={message.type} dismissible onClose={() => setMessage({ type: '', text: '' })}>
          {message.text}
        </Alert>
      )}

      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Select Collection and Request</h5>
        </Card.Header>
        <Card.Body>
          <Form.Group className="mb-3">
            <Form.Label>Postman Collection</Form.Label>
            <Form.Select
              value={selectedCollectionId}
              onChange={(e) => setSelectedCollectionId(e.target.value)}
              disabled={loading}
            >
              <option value="">-- Select a collection --</option>
              {collections.map((collection) => (
                <option key={collection.id} value={collection.id}>
                  {collection.name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          {selectedCollectionId && (
            <Form.Group className="mb-3">
              <Form.Label>Request</Form.Label>
              {loadingRequests ? (
                <div className="text-center py-2">
                  <Spinner animation="border" size="sm" className="me-2" />
                  Loading requests...
                </div>
              ) : (
                <Form.Select
                  value={selectedRequestId}
                  onChange={(e) => setSelectedRequestId(e.target.value)}
                >
                  <option value="">-- Select a request --</option>
                  {requests.map((request) => (
                    <option key={request.id} value={request.id}>
                      {request.name} ({request.request?.method || 'N/A'}) - {request.path}
                    </option>
                  ))}
                </Form.Select>
              )}
            </Form.Group>
          )}
        </Card.Body>
      </Card>

      {selectedRequest && (
        <Card>
          <Card.Header className="d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Request Details</h5>
            <Button
              variant="primary"
              onClick={handleSaveRequest}
              disabled={saving}
            >
              {saving ? (
                <>
                  <Spinner animation="border" size="sm" className="me-2" />
                  Saving...
                </>
              ) : (
                'Save Request'
              )}
            </Button>
          </Card.Header>
          <Card.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Request Name</Form.Label>
                <Form.Control
                  type="text"
                  value={requestDetails.name}
                  onChange={(e) => setRequestDetails({ ...requestDetails, name: e.target.value })}
                  placeholder="Request name"
                />
              </Form.Group>

              <div className="row">
                <Form.Group className="mb-3 col-md-6">
                  <Form.Label>Method</Form.Label>
                  <Form.Select
                    value={requestDetails.method}
                    onChange={(e) => setRequestDetails({ ...requestDetails, method: e.target.value })}
                  >
                    <option value="GET">GET</option>
                    <option value="POST">POST</option>
                    <option value="PUT">PUT</option>
                    <option value="PATCH">PATCH</option>
                    <option value="DELETE">DELETE</option>
                    <option value="HEAD">HEAD</option>
                    <option value="OPTIONS">OPTIONS</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3 col-md-6">
                  <Form.Label>URL</Form.Label>
                  <Form.Control
                    type="text"
                    value={requestDetails.url}
                    onChange={(e) => setRequestDetails({ ...requestDetails, url: e.target.value })}
                    placeholder="https://api.example.com/v1/endpoint"
                  />
                </Form.Group>
              </div>

              <Form.Group className="mb-3">
                <Form.Label>Description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={requestDetails.description}
                  onChange={(e) => setRequestDetails({ ...requestDetails, description: e.target.value })}
                  placeholder="Request description"
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Headers (JSON Array)</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={6}
                  value={requestDetails.headers}
                  onChange={(e) => setRequestDetails({ ...requestDetails, headers: e.target.value })}
                  placeholder='[{"key": "Content-Type", "value": "application/json", "type": "text"}]'
                />
                <Form.Text className="text-muted">
                  Enter headers as a JSON array
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Query Parameters (JSON Array)</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  value={requestDetails.queryParams}
                  onChange={(e) => setRequestDetails({ ...requestDetails, queryParams: e.target.value })}
                  placeholder='[{"key": "param1", "value": "value1", "type": "string"}]'
                />
                <Form.Text className="text-muted">
                  Enter query parameters as a JSON array
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Body</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={10}
                  value={requestDetails.body}
                  onChange={(e) => setRequestDetails({ ...requestDetails, body: e.target.value })}
                  placeholder="Request body content (JSON, XML, etc.)"
                />
                <Form.Text className="text-muted">
                  Enter request body content
                </Form.Text>
              </Form.Group>
            </Form>
          </Card.Body>
        </Card>
      )}

      {selectedRequest && responses.length > 0 && (
        <Card className="mt-4">
          <Card.Header className="d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Response Objects ({responses.length})</h5>
            <div className="d-flex gap-2 align-items-center">
              <Form.Group className="mb-0" style={{ minWidth: '250px' }}>
                <Form.Select
                  value={selectedResponseStatus}
                  onChange={(e) => {
                    const status = e.target.value;
                    setSelectedResponseStatus(status);
                    if (status) {
                      // Find response by status code
                      const found = responses.find(r => {
                        const code = r.code || r.status;
                        return String(code) === String(status);
                      });
                      setFilteredResponse(found || null);
                    } else {
                      setFilteredResponse(null);
                    }
                  }}
                >
                  <option value="">-- All Responses --</option>
                  {responses.map((response, index) => {
                    const statusCode = response.code || response.status || 'N/A';
                    return (
                      <option key={index} value={statusCode}>
                        {statusCode} - {response.name || `Response ${index + 1}`}
                      </option>
                    );
                  })}
                </Form.Select>
              </Form.Group>
              {selectedResponseStatus && (
                <Button
                  variant="outline-secondary"
                  size="sm"
                  onClick={() => {
                    setSelectedResponseStatus('');
                    setFilteredResponse(null);
                  }}
                >
                  Clear Filter
                </Button>
              )}
            </div>
          </Card.Header>
          <Card.Body>
            {selectedResponseStatus && filteredResponse ? (
              <div>
                <Alert variant="info" className="mb-3 d-flex justify-content-between align-items-center">
                  <div>
                    <strong>Loaded Response Model:</strong> {filteredResponse.name || `Response ${filteredResponse.code || filteredResponse.status}`}
                    <Badge bg={filteredResponse.code === 200 ? 'success' : filteredResponse.code >= 400 ? 'danger' : 'warning'} className="ms-2">
                      {filteredResponse.code || filteredResponse.status}
                    </Badge>
                  </div>
                  <Button
                    variant="outline-primary"
                    size="sm"
                    onClick={() => {
                      setSelectedResponse(filteredResponse);
                      setShowResponseModal(true);
                    }}
                  >
                    Open in Modal
                  </Button>
                </Alert>
                <ResponseDetailsView response={filteredResponse} />
              </div>
            ) : (
              <div className="table-responsive">
                <Table striped bordered hover>
                  <thead>
                    <tr>
                      <th>Status Code</th>
                      <th>Status</th>
                      <th>Name</th>
                      <th>Body Preview</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {responses.map((response, index) => {
                      const statusCode = response.code || response.status || 'N/A';
                      const is200 = statusCode === 200 || statusCode === '200';
                      const bodyPreview = response.body 
                        ? (typeof response.body === 'string' 
                            ? response.body.substring(0, 100) 
                            : JSON.stringify(response.body).substring(0, 100))
                        : 'No body';
                      
                      return (
                        <tr key={index} className={is200 ? 'table-success' : ''}>
                          <td>
                            <Badge bg={is200 ? 'success' : statusCode >= 400 ? 'danger' : 'warning'}>
                              {statusCode}
                            </Badge>
                          </td>
                          <td>{response.status || 'N/A'}</td>
                          <td>{response.name || `Response ${index + 1}`}</td>
                          <td className="text-truncate" style={{ maxWidth: '300px' }}>
                            {bodyPreview}
                            {bodyPreview.length >= 100 && '...'}
                          </td>
                          <td>
                            <Button
                              variant="outline-primary"
                              size="sm"
                              onClick={() => {
                                setSelectedResponse(response);
                                setShowResponseModal(true);
                              }}
                              className="me-2"
                            >
                              View Details
                            </Button>
                            <Button
                              variant="outline-success"
                              size="sm"
                              onClick={() => {
                                setSelectedResponseStatus(statusCode);
                                setFilteredResponse(response);
                              }}
                            >
                              Load Model
                            </Button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              </div>
            )}
          </Card.Body>
        </Card>
      )}

      {selectedRequest && responses.length === 0 && (
        <Card className="mt-4">
          <Card.Header className="d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Response Objects</h5>
            <Button
              variant="outline-primary"
              size="sm"
              onClick={() => {
                if (selectedRequest) {
                  loadRequestWithResponses();
                }
              }}
            >
              Refresh Responses
            </Button>
          </Card.Header>
          <Card.Body>
            <Alert variant="info">
              <strong>No response objects found for this request.</strong>
              <br />
              <small className="text-muted">
                This request may not have response examples defined in the collection. 
                Responses are typically added during Swagger to Postman conversion.
              </small>
            </Alert>
            {fullRequest && (
              <div className="mt-3">
                <details>
                  <summary className="text-muted" style={{ cursor: 'pointer' }}>
                    Debug Info (Click to expand)
                  </summary>
                  <pre className="mt-2 p-2 bg-light" style={{ fontSize: '12px', maxHeight: '200px', overflow: 'auto' }}>
                    {JSON.stringify({
                      requestName: fullRequest.name,
                      requestMethod: fullRequest.request?.method,
                      hasResponse: !!fullRequest.response,
                      responseType: typeof fullRequest.response,
                      responseIsArray: Array.isArray(fullRequest.response),
                      responseLength: fullRequest.response?.length || 0,
                      responseKeys: fullRequest.response && fullRequest.response.length > 0 ? Object.keys(fullRequest.response[0]) : []
                    }, null, 2)}
                  </pre>
                </details>
              </div>
            )}
            {!fullRequest && (
              <Alert variant="warning" className="mt-3">
                <strong>Request not found in full collection.</strong>
                <br />
                <small>
                  The request might be in a folder structure. Try refreshing or check the console for debugging information.
                </small>
              </Alert>
            )}
          </Card.Body>
        </Card>
      )}

      {/* Response Details Modal */}
      <ResponseDetailsModal
        response={selectedResponse}
        show={showResponseModal}
        onHide={() => {
          setShowResponseModal(false);
          setSelectedResponse(null);
        }}
      />
    </div>
  );
};

// Response Details View Component (for inline display)
const ResponseDetailsView = ({ response }) => {
  if (!response) return null;

  const statusCode = response.code || response.status || 'N/A';
  const headers = response.header || [];
  const body = response.body || '';
  const bodyText = typeof body === 'string' ? body : JSON.stringify(body, null, 2);
  let parsedBody = null;
  try {
    parsedBody = typeof body === 'string' ? JSON.parse(body) : body;
  } catch (e) {
    // Not JSON, use as string
  }

  return (
    <div>
      <Accordion defaultActiveKey="1">
        <Accordion.Item eventKey="0">
          <Accordion.Header>
            <strong>Headers</strong> ({headers.length} header{headers.length !== 1 ? 's' : ''})
          </Accordion.Header>
          <Accordion.Body>
            {headers.length > 0 ? (
              <div>
                <Table striped bordered size="sm">
                  <thead>
                    <tr>
                      <th>Key</th>
                      <th>Value</th>
                      <th>Type</th>
                    </tr>
                  </thead>
                  <tbody>
                    {headers.map((header, idx) => (
                      <tr key={idx}>
                        <td><strong>{header.key}</strong></td>
                        <td>{header.value}</td>
                        <td>{header.type || 'text'}</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
                <pre style={{ maxHeight: '200px', overflow: 'auto', marginTop: '15px', fontSize: '12px' }}>
                  {JSON.stringify(headers, null, 2)}
                </pre>
              </div>
            ) : (
              <Alert variant="info">No headers defined</Alert>
            )}
          </Accordion.Body>
        </Accordion.Item>
        <Accordion.Item eventKey="1">
          <Accordion.Header>
            <strong>Response Body</strong> {parsedBody && <Badge bg="info" className="ms-2">JSON</Badge>}
          </Accordion.Header>
          <Accordion.Body>
            {body ? (
              <div>
                {parsedBody ? (
                  <div>
                    <h6 className="mb-3">Formatted JSON:</h6>
                    <pre style={{ maxHeight: '500px', overflow: 'auto', backgroundColor: '#f8f9fa', padding: '15px', borderRadius: '5px' }}>
                      {JSON.stringify(parsedBody, null, 2)}
                    </pre>
                  </div>
                ) : (
                  <pre style={{ maxHeight: '500px', overflow: 'auto', whiteSpace: 'pre-wrap', backgroundColor: '#f8f9fa', padding: '15px', borderRadius: '5px' }}>
                    {bodyText}
                  </pre>
                )}
              </div>
            ) : (
              <Alert variant="info">No body content</Alert>
            )}
          </Accordion.Body>
        </Accordion.Item>
        {response.originalRequest && (
          <Accordion.Item eventKey="2">
            <Accordion.Header>
              <strong>Original Request</strong>
            </Accordion.Header>
            <Accordion.Body>
              <pre style={{ maxHeight: '300px', overflow: 'auto' }}>
                {JSON.stringify(response.originalRequest, null, 2)}
              </pre>
            </Accordion.Body>
          </Accordion.Item>
        )}
        {response._postman_previewlanguage && (
          <Accordion.Item eventKey="3">
            <Accordion.Header>
              <strong>Preview Language</strong>
            </Accordion.Header>
            <Accordion.Body>
              {response._postman_previewlanguage}
            </Accordion.Body>
          </Accordion.Item>
        )}
      </Accordion>
    </div>
  );
};

// Response Details Modal Component
const ResponseDetailsModal = ({ response, show, onHide }) => {
  if (!response) return null;

  const statusCode = response.code || response.status || 'N/A';
  const headers = response.header || [];
  const body = response.body || '';
  const bodyText = typeof body === 'string' ? body : JSON.stringify(body, null, 2);

  return (
    <Modal show={show} onHide={onHide} size="lg">
      <Modal.Header closeButton>
        <Modal.Title>
          Response Details - {response.name || 'Response'} 
          <Badge bg={statusCode === 200 ? 'success' : 'warning'} className="ms-2">
            {statusCode}
          </Badge>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <ResponseDetailsView response={response} />
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default GridFiltering;
