import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert, Spinner, Table, Badge, Accordion, Modal } from 'react-bootstrap';
import apiService from '../services/api.service';

const GridFiltering = () => {
  const [collections, setCollections] = useState([]);
  const [selectedCollectionId, setSelectedCollectionId] = useState('');
  const [requests, setRequests] = useState([]);
  const [selectedRequestId, setSelectedRequestId] = useState('');
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [fullRequest, setFullRequest] = useState(null); // Full request with responses
  const [loading, setLoading] = useState(false);
  const [loadingRequests, setLoadingRequests] = useState(false);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  
  // Editable request details
  const [requestDetails, setRequestDetails] = useState({
    name: '',
    method: '',
    url: '',
    headers: '',
    body: '',
    description: '',
    queryParams: ''
  });
  
  // Responses
  const [responses, setResponses] = useState([]);
  const [selectedResponse, setSelectedResponse] = useState(null);
  const [showResponseModal, setShowResponseModal] = useState(false);

  useEffect(() => {
    loadCollections();
  }, []);

  useEffect(() => {
    if (selectedCollectionId) {
      loadRequests();
    } else {
      setRequests([]);
      setSelectedRequestId('');
      setSelectedRequest(null);
      setRequestDetails({
        name: '',
        method: '',
        url: '',
        headers: '',
        body: '',
        description: '',
        queryParams: ''
      });
    }
  }, [selectedCollectionId]);

  useEffect(() => {
    if (selectedRequestId && requests.length > 0) {
      const request = requests.find(r => r.id === selectedRequestId);
      if (request) {
        setSelectedRequest(request);
        loadRequestDetails(request);
        loadRequestWithResponses(request);
      }
    } else {
      setSelectedRequest(null);
      setFullRequest(null);
      setResponses([]);
      setRequestDetails({
        name: '',
        method: '',
        url: '',
        headers: '',
        body: '',
        description: '',
        queryParams: ''
      });
    }
  }, [selectedRequestId, requests]);

  const loadCollections = async () => {
    try {
      const response = await apiService.get('/api/collections');
      setCollections(response.collections || []);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load collections' });
    }
  };

  const loadRequests = async () => {
    if (!selectedCollectionId) return;
    
    setLoadingRequests(true);
    setMessage({ type: '', text: '' });
    try {
      const response = await apiService.get(`/api/collections/${selectedCollectionId}/requests`);
      const allRequests = flattenItems(response.items || []);
      setRequests(allRequests);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load requests' });
    } finally {
      setLoadingRequests(false);
    }
  };

  const flattenItems = (items, parentPath = '') => {
    const requests = [];
    items.forEach((item, index) => {
      const currentPath = parentPath ? `${parentPath} > ${item.name}` : item.name;
      
      if (item.request) {
        // It's a request
        requests.push({
          ...item,
          id: `${parentPath}-${index}-${Date.now()}`,
          path: currentPath
        });
      } else if (item.item && Array.isArray(item.item)) {
        // It's a folder, recursively get requests
        const folderRequests = flattenItems(item.item, currentPath);
        requests.push(...folderRequests);
      }
    });
    return requests;
  };

  const loadRequestDetails = (request) => {
    if (!request || !request.request) return;

    try {
      const req = request.request;
      const url = req.url || {};
      const headers = req.header || [];
      const body = req.body || {};
      const queryParams = url.query || [];

      setRequestDetails({
        name: request.name || '',
        method: req.method || '',
        url: url.raw || '',
        headers: JSON.stringify(headers, null, 2),
        body: body.raw || (body ? JSON.stringify(body, null, 2) : ''),
        description: req.description || '',
        queryParams: JSON.stringify(queryParams, null, 2)
      });
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to load request details' });
    }
  };

  const loadRequestWithResponses = async () => {
    if (!selectedCollectionId || !selectedRequest) return;

    try {
      // Load full collection to get responses
      const fullResponse = await apiService.get(`/api/collections/${selectedCollectionId}/full`);
      const fullCollection = fullResponse.items || [];
      
      // Find the request in the full collection to get responses
      const foundRequest = findRequestInFullCollection(fullCollection, selectedRequest);
      
      if (foundRequest) {
        setFullRequest(foundRequest);
        const requestResponses = foundRequest.response || [];
        setResponses(requestResponses);
      } else {
        setFullRequest(null);
        setResponses([]);
      }
    } catch (error) {
      console.error('Failed to load request responses:', error);
      setResponses([]);
    }
  };

  const findRequestInFullCollection = (items, targetRequest) => {
    const findRecursive = (items) => {
      for (const item of items) {
        if (item.item && Array.isArray(item.item)) {
          // It's a folder - recurse
          const found = findRecursive(item.item);
          if (found) return found;
        } else if (item.request) {
          // It's a request - check if it matches
          if (item.name === targetRequest.name && 
              item.request.method === targetRequest.request?.method) {
            return item;
          }
        }
      }
      return null;
    };

    return findRecursive(items);
  };

  const handleSaveRequest = async () => {
    if (!selectedCollectionId || !selectedRequestId || !selectedRequest) {
      setMessage({ type: 'warning', text: 'Please select a collection and request first' });
      return;
    }

    setSaving(true);
    setMessage({ type: '', text: '' });

    try {
      // Parse headers and query params
      let headers = [];
      let queryParams = [];
      
      try {
        if (requestDetails.headers.trim()) {
          headers = JSON.parse(requestDetails.headers);
          if (!Array.isArray(headers)) {
            throw new Error('Headers must be a JSON array');
          }
        }
      } catch (e) {
        setMessage({ type: 'danger', text: 'Invalid JSON format in headers. Must be a valid JSON array.' });
        setSaving(false);
        return;
      }

      try {
        if (requestDetails.queryParams.trim()) {
          queryParams = JSON.parse(requestDetails.queryParams);
          if (!Array.isArray(queryParams)) {
            throw new Error('Query params must be a JSON array');
          }
        }
      } catch (e) {
        setMessage({ type: 'danger', text: 'Invalid JSON format in query params. Must be a valid JSON array.' });
        setSaving(false);
        return;
      }

      // Load full collection to update
      const fullResponse = await apiService.get(`/api/collections/${selectedCollectionId}/full`);
      const fullCollection = {
        info: fullResponse.collection_info || {},
        item: fullResponse.items || []
      };

      // Find and update the request in the collection
      const updated = updateRequestInCollection(
        fullCollection.item,
        selectedRequest,
        requestDetails,
        headers,
        queryParams
      );

      if (!updated) {
        setMessage({ type: 'danger', text: 'Request not found in collection' });
        setSaving(false);
        return;
      }

      // Save the updated collection
      await apiService.put(`/api/collections/${selectedCollectionId}`, {
        collection: fullCollection
      });

      setMessage({ type: 'success', text: 'Request updated successfully!' });
      
      // Reload requests to reflect changes
      await loadRequests();
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Failed to save request' });
    } finally {
      setSaving(false);
    }
  };

  const updateRequestInCollection = (items, targetRequest, details, headers, queryParams) => {
    const findAndUpdate = (items) => {
      for (const item of items) {
        if (item.item && Array.isArray(item.item)) {
          // It's a folder - recurse
          if (findAndUpdate(item.item)) {
            return true;
          }
        } else if (item.request) {
          // It's a request - check if it matches
          if (item.name === targetRequest.name && 
              item.request.method === targetRequest.request?.method) {
            // Update the request
            item.name = details.name;
            item.request.method = details.method;
            item.request.description = details.description;
            item.request.header = headers;
            
            // Update URL
            if (item.request.url) {
              item.request.url.raw = details.url;
              item.request.url.query = queryParams;
              
              // Parse URL - handle Postman variables like {{baseUrl}}
              try {
                // Check if URL contains variables (e.g., {{baseUrl}})
                if (details.url.includes('{{')) {
                  // For URLs with variables, try to parse manually
                  const urlMatch = details.url.match(/^(https?:\/\/[^\/]+|{{[^}]+}})(\/.*)?$/);
                  if (urlMatch) {
                    const hostPart = urlMatch[1];
                    const pathPart = urlMatch[2] || '';
                    
                    if (hostPart.startsWith('{{')) {
                      // Variable-based URL
                      item.request.url.host = [hostPart];
                    } else {
                      // Regular URL
                      item.request.url.host = [hostPart.replace(/^https?:\/\//, '')];
                    }
                    
                    // Parse path
                    if (pathPart) {
                      item.request.url.path = pathPart.split('/').filter(p => p && !p.startsWith('{{'));
                    } else {
                      item.request.url.path = [];
                    }
                  }
                } else {
                  // Regular URL without variables
                  const urlObj = new URL(details.url);
                  item.request.url.host = [urlObj.hostname];
                  item.request.url.path = urlObj.pathname.split('/').filter(p => p);
                }
              } catch (e) {
                // If URL parsing fails, try to parse manually
                const urlMatch = details.url.match(/^(https?:\/\/[^\/]+|{{[^}]+}})(\/.*)?$/);
                if (urlMatch) {
                  const hostPart = urlMatch[1];
                  if (hostPart.startsWith('{{')) {
                    item.request.url.host = [hostPart];
                  } else {
                    item.request.url.host = [hostPart.replace(/^https?:\/\//, '')];
                  }
                  const pathPart = urlMatch[2] || '';
                  item.request.url.path = pathPart ? pathPart.split('/').filter(p => p && !p.startsWith('{{')) : [];
                }
              }
            }
            
            // Update body
            if (details.body && details.body.trim()) {
              if (item.request.body) {
                item.request.body.raw = details.body;
              } else {
                item.request.body = {
                  mode: 'raw',
                  raw: details.body,
                  options: {
                    raw: {
                      language: 'json'
                    }
                  }
                };
              }
            }
            
            return true;
          }
        }
      }
      return false;
    };

    return findAndUpdate(items);
  };

  return (
    <div>
      <h2 className="mb-4">Grid Filtering</h2>
      
      {message.text && (
        <Alert variant={message.type} dismissible onClose={() => setMessage({ type: '', text: '' })}>
          {message.text}
        </Alert>
      )}

      <Card className="mb-4">
        <Card.Header>
          <h5 className="mb-0">Select Collection and Request</h5>
        </Card.Header>
        <Card.Body>
          <Form.Group className="mb-3">
            <Form.Label>Postman Collection</Form.Label>
            <Form.Select
              value={selectedCollectionId}
              onChange={(e) => setSelectedCollectionId(e.target.value)}
              disabled={loading}
            >
              <option value="">-- Select a collection --</option>
              {collections.map((collection) => (
                <option key={collection.id} value={collection.id}>
                  {collection.name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          {selectedCollectionId && (
            <Form.Group className="mb-3">
              <Form.Label>Request</Form.Label>
              {loadingRequests ? (
                <div className="text-center py-2">
                  <Spinner animation="border" size="sm" className="me-2" />
                  Loading requests...
                </div>
              ) : (
                <Form.Select
                  value={selectedRequestId}
                  onChange={(e) => setSelectedRequestId(e.target.value)}
                >
                  <option value="">-- Select a request --</option>
                  {requests.map((request) => (
                    <option key={request.id} value={request.id}>
                      {request.name} ({request.request?.method || 'N/A'}) - {request.path}
                    </option>
                  ))}
                </Form.Select>
              )}
            </Form.Group>
          )}
        </Card.Body>
      </Card>

      {selectedRequest && (
        <Card>
          <Card.Header className="d-flex justify-content-between align-items-center">
            <h5 className="mb-0">Request Details</h5>
            <Button
              variant="primary"
              onClick={handleSaveRequest}
              disabled={saving}
            >
              {saving ? (
                <>
                  <Spinner animation="border" size="sm" className="me-2" />
                  Saving...
                </>
              ) : (
                'Save Request'
              )}
            </Button>
          </Card.Header>
          <Card.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Request Name</Form.Label>
                <Form.Control
                  type="text"
                  value={requestDetails.name}
                  onChange={(e) => setRequestDetails({ ...requestDetails, name: e.target.value })}
                  placeholder="Request name"
                />
              </Form.Group>

              <div className="row">
                <Form.Group className="mb-3 col-md-6">
                  <Form.Label>Method</Form.Label>
                  <Form.Select
                    value={requestDetails.method}
                    onChange={(e) => setRequestDetails({ ...requestDetails, method: e.target.value })}
                  >
                    <option value="GET">GET</option>
                    <option value="POST">POST</option>
                    <option value="PUT">PUT</option>
                    <option value="PATCH">PATCH</option>
                    <option value="DELETE">DELETE</option>
                    <option value="HEAD">HEAD</option>
                    <option value="OPTIONS">OPTIONS</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3 col-md-6">
                  <Form.Label>URL</Form.Label>
                  <Form.Control
                    type="text"
                    value={requestDetails.url}
                    onChange={(e) => setRequestDetails({ ...requestDetails, url: e.target.value })}
                    placeholder="https://api.example.com/v1/endpoint"
                  />
                </Form.Group>
              </div>

              <Form.Group className="mb-3">
                <Form.Label>Description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={requestDetails.description}
                  onChange={(e) => setRequestDetails({ ...requestDetails, description: e.target.value })}
                  placeholder="Request description"
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Headers (JSON Array)</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={6}
                  value={requestDetails.headers}
                  onChange={(e) => setRequestDetails({ ...requestDetails, headers: e.target.value })}
                  placeholder='[{"key": "Content-Type", "value": "application/json", "type": "text"}]'
                />
                <Form.Text className="text-muted">
                  Enter headers as a JSON array
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Query Parameters (JSON Array)</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  value={requestDetails.queryParams}
                  onChange={(e) => setRequestDetails({ ...requestDetails, queryParams: e.target.value })}
                  placeholder='[{"key": "param1", "value": "value1", "type": "string"}]'
                />
                <Form.Text className="text-muted">
                  Enter query parameters as a JSON array
                </Form.Text>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Body</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={10}
                  value={requestDetails.body}
                  onChange={(e) => setRequestDetails({ ...requestDetails, body: e.target.value })}
                  placeholder="Request body content (JSON, XML, etc.)"
                />
                <Form.Text className="text-muted">
                  Enter request body content
                </Form.Text>
              </Form.Group>
            </Form>
          </Card.Body>
        </Card>
      )}

      {selectedRequest && responses.length > 0 && (
        <Card className="mt-4">
          <Card.Header>
            <h5 className="mb-0">Response Objects</h5>
          </Card.Header>
          <Card.Body>
            <div className="table-responsive">
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Status Code</th>
                    <th>Status</th>
                    <th>Name</th>
                    <th>Body Preview</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {responses.map((response, index) => {
                    const statusCode = response.code || response.status || 'N/A';
                    const is200 = statusCode === 200 || statusCode === '200';
                    const bodyPreview = response.body 
                      ? (typeof response.body === 'string' 
                          ? response.body.substring(0, 100) 
                          : JSON.stringify(response.body).substring(0, 100))
                      : 'No body';
                    
                    return (
                      <tr key={index} className={is200 ? 'table-success' : ''}>
                        <td>
                          <Badge bg={is200 ? 'success' : statusCode >= 400 ? 'danger' : 'warning'}>
                            {statusCode}
                          </Badge>
                        </td>
                        <td>{response.status || 'N/A'}</td>
                        <td>{response.name || `Response ${index + 1}`}</td>
                        <td className="text-truncate" style={{ maxWidth: '300px' }}>
                          {bodyPreview}
                          {bodyPreview.length >= 100 && '...'}
                        </td>
                        <td>
                          <Button
                            variant="outline-primary"
                            size="sm"
                            onClick={() => {
                              setSelectedResponse(response);
                              setShowResponseModal(true);
                            }}
                          >
                            View Details
                          </Button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </Table>
            </div>
          </Card.Body>
        </Card>
      )}

      {selectedRequest && responses.length === 0 && (
        <Card className="mt-4">
          <Card.Body>
            <Alert variant="info">No response objects found for this request.</Alert>
          </Card.Body>
        </Card>
      )}

      {/* Response Details Modal */}
      <ResponseDetailsModal
        response={selectedResponse}
        show={showResponseModal}
        onHide={() => {
          setShowResponseModal(false);
          setSelectedResponse(null);
        }}
      />
    </div>
  );
};

// Response Details Modal Component
const ResponseDetailsModal = ({ response, show, onHide }) => {
  if (!response) return null;

  const statusCode = response.code || response.status || 'N/A';
  const headers = response.header || [];
  const body = response.body || '';
  const bodyText = typeof body === 'string' ? body : JSON.stringify(body, null, 2);

  return (
    <Modal show={show} onHide={onHide} size="lg">
      <Modal.Header closeButton>
        <Modal.Title>
          Response Details - {response.name || 'Response'} 
          <Badge bg={statusCode === 200 ? 'success' : 'warning'} className="ms-2">
            {statusCode}
          </Badge>
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Accordion defaultActiveKey="0">
          <Accordion.Item eventKey="0">
            <Accordion.Header>Headers</Accordion.Header>
            <Accordion.Body>
              <pre style={{ maxHeight: '300px', overflow: 'auto' }}>
                {JSON.stringify(headers, null, 2)}
              </pre>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="1">
            <Accordion.Header>Body</Accordion.Header>
            <Accordion.Body>
              <pre style={{ maxHeight: '400px', overflow: 'auto', whiteSpace: 'pre-wrap' }}>
                {bodyText}
              </pre>
            </Accordion.Body>
          </Accordion.Item>
          {response._postman_previewlanguage && (
            <Accordion.Item eventKey="2">
              <Accordion.Header>Preview Language</Accordion.Header>
              <Accordion.Body>
                {response._postman_previewlanguage}
              </Accordion.Body>
            </Accordion.Item>
          )}
        </Accordion>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default GridFiltering;
